#!/usr/bin/env node
const commander=require("commander"),colors=require("colors"),search=require("./utils/search");commander.version("1.0.0","-v, --version").option("-t, --channel [channel]","选择文档类目，可选项目有 nodejs ").action(function({channel:o}){let e="Node.js";switch(o){case"nodejs":e="Node.js";break;default:this.channel="nodejs",e="Node.js"}console.log(colors.rainbow("\n-############################# ")+colors.yellow.bold("当前处于 "+e+" 频道")+colors.rainbow(" ###############################-\n"))}),commander.action(function(o){"string"==typeof o&&search(o,this.channel)}),commander.parse(process.argv);